mapper.update:
UPDATE tb_cmp_card_audit
code=#{code}, taxid=#{taxid}, name=#{name}, address=#{address}, telephone=#{telephone}, bank=#{bank}, account=#{account}, source=#{source}, type=#{type}, note=#{note}, cert=#{cert},
WHERE id=#{id}

mapper.select:

    SELECT * FROM tb_cmp_card_audit A


            A.code=#{paramObj.code}
            AND A.source=#{paramObj.source}
            AND A.type=#{paramObj.type}
            AND A.createtime > str_to_date(#{paramObj.dateRangStart},
                '%Y-%m-%d %H:%i:%s')

            AND A.createtime < str_to_date(#{paramObj.dateRangEnd}, '%Y-%m-%d
                %H:%i:%s')

            AND A.code IS NOT NULL
            AND A.code IS NULL
            AND A.status=#{paramObj.status}





    SELECT A.* FROM tb_cmp_card_audit A INNER JOIN
    ( SELECT DISTINCT code,serviceid FROM tb_code_taxid_serviceid
    WHERE

        serviceid IN(${otherParams.serviceIds})


        1=2

    ) AS R
    ON A.code = R.code


            A.code=#{paramObj.code}
            AND A.source=#{paramObj.source}
            AND A.type=#{paramObj.type}
            AND A.createtime > str_to_date(#{paramObj.dateRangStart},
                '%Y-%m-%d %H:%i:%s')

            AND A.createtime < str_to_date(#{paramObj.dateRangEnd}, '%Y-%m-%d
                %H:%i:%s')

            AND A.code IS NOT NULL
            AND A.code IS NULL
            AND A.status=#{paramObj.status}


    UNION ALL
    SELECT A.* FROM tb_cmp_card_audit A INNER JOIN
    ( SELECT DISTINCT taxid,serviceid FROM tb_code_taxid_serviceid
    WHERE

        serviceid IN(${otherParams.serviceIds})
        AND code IS NULL


        1=2

    ) AS R
    ON A.taxid = R.taxid


            A.code=#{paramObj.code}
            AND A.source=#{paramObj.source}
            AND A.type=#{paramObj.type}
            AND A.createtime > str_to_date(#{paramObj.dateRangStart},
                '%Y-%m-%d %H:%i:%s')

            AND A.createtime < str_to_date(#{paramObj.dateRangEnd}, '%Y-%m-%d
                %H:%i:%s')

            AND A.code IS NOT NULL
            AND A.code IS NULL
            AND A.status=#{paramObj.status}




    ORDER BY ${orderCol}

LIMIT ${startIndex},${pageSize}

select.if:
SELECT COUNT(*) FROM tb_cmp_card_audit A


        A.code=#{paramObj.code}
        AND A.source=#{paramObj.source}
        AND A.type=#{paramObj.type}
        AND A.createtime > str_to_date(#{paramObj.dateRangStart},
            '%Y-%m-%d %H:%i:%s')

        AND A.createtime < str_to_date(#{paramObj.dateRangEnd}, '%Y-%m-%d
            %H:%i:%s')

        AND A.code IS NOT NULL
        AND A.code IS NULL
        AND A.status=#{paramObj.status}

SELECT SUM(NUM) FROM
(SELECT COUNT(*) AS NUM FROM tb_cmp_card_audit A INNER JOIN
( SELECT DISTINCT code,serviceid FROM tb_code_taxid_serviceid
WHERE

    serviceid IN(${otherParams.serviceIds})


    1=2

) AS R
ON A.code = R.code


        A.code=#{paramObj.code}
        AND A.source=#{paramObj.source}
        AND A.type=#{paramObj.type}
        AND A.createtime > str_to_date(#{paramObj.dateRangStart},
            '%Y-%m-%d %H:%i:%s')

        AND A.createtime < str_to_date(#{paramObj.dateRangEnd}, '%Y-%m-%d
            %H:%i:%s')

        AND A.code IS NOT NULL
        AND A.code IS NULL
        AND A.status=#{paramObj.status}


UNION ALL
SELECT COUNT(*) AS NUM FROM tb_cmp_card_audit A INNER JOIN
( SELECT DISTINCT taxid,serviceid FROM tb_code_taxid_serviceid
WHERE

    serviceid IN(${otherParams.serviceIds}
    AND code IS NULL)


    1=2

) AS R
ON A.taxid = R.taxid


        A.code=#{paramObj.code}
        AND A.source=#{paramObj.source}
        AND A.type=#{paramObj.type}
        AND A.createtime > str_to_date(#{paramObj.dateRangStart},
            '%Y-%m-%d %H:%i:%s')

        AND A.createtime < str_to_date(#{paramObj.dateRangEnd}, '%Y-%m-%d
            %H:%i:%s')

        AND A.code IS NOT NULL
        AND A.code IS NULL
        AND A.status=#{paramObj.status}


)AS TEMP_TABLE
